    document.addEventListener('DOMContentLoaded', () => {
        const dataContainer = document.querySelector('.status-bar-data-container');
        if (!dataContainer) return;

        function parseData(text) {
            const data = { blocks: [] };
            const blockRegex = /\[([^\]]+)\]([\s\S]*?)\[\/\1\]/g;
            let match;
            while ((match = blockRegex.exec(text)) !== null) {
                const blockName = match[1];
                const blockContent = match[2].trim();
                const items = {};
                const lines = blockContent.split('\n');
                let currentKey = null;
                let currentValue = '';
                let isMultiLine = false;

                lines.forEach(line => {
                    const parts = line.match(/^([^:]+):\s*(.*)/);
                    if (parts) {
                        if (currentKey) items[currentKey] = currentValue.trim();
                        currentKey = parts[1].trim();
                        currentValue = parts[2].trim();
                        isMultiLine = false;
                        if (currentValue === '') isMultiLine = true;
                    } else if (currentKey && isMultiLine) {
                        currentValue += (currentValue ? '\n' : '') + line.trim();
                    }
                });
                if (currentKey) items[currentKey] = currentValue.trim();
                data.blocks.push({ name: blockName, items: items });
            }
            return data;
        }

        function renderData(data) {
            const tabsContainer = document.querySelector('.tabs-container');
            const contentArea = document.querySelector('.status-bar-content');
            if (!tabsContainer || !contentArea) return;

            tabsContainer.innerHTML = '';
            contentArea.innerHTML = '';

            data.blocks.forEach((block, index) => {
                // 跳过config块
                if (block.name.toLowerCase() === 'config') return;

                const tab = document.createElement('div');
                tab.className = 'tab';
                tab.textContent = block.name;
                tab.dataset.target = `panel-${index}`;
                tabsContainer.appendChild(tab);

                const panel = document.createElement('div');
                panel.id = `panel-${index}`;
                panel.className = 'content-panel';

                let panelHTML = '';
                for (const key in block.items) {
                    const value = block.items[key];
                    if (key === '背包') {
                        const items = value.split('\n').map(item => `<li>${item.trim().replace(/^- /,'')}</li>`).join('');
                        panelHTML += `<div class="data-item"><span class="data-key">背包:</span></div><ul class="item-list">${items}</ul>`;
                    } else if (key === '好感度') {
                        const [current, max] = value.split('/');
                        const percentage = (parseFloat(current) / parseFloat(max)) * 100;
                        panelHTML += `
                            <div class="data-item">
                                <span class="data-key">好感度:</span>
                                <span class="data-value">${value}</span>
                            </div>
                            <div class="progress-bar">
                                <div class="progress-bar-fill" style="width: ${percentage}%;">${current}</div>
                            </div>`;
                    } else if (key === '内心想法' || key === '系统建议') {
                         const thoughtMatch = value.match(/「(.*?)」/s);
                         const commentMatch = value.match(/\(系统点评：(.*?)\)/s);
                         if (thoughtMatch && commentMatch) {
                            panelHTML += `<div class="system-quote"><div class="inner-thought">「${thoughtMatch[1]}」</div><span class="system-comment">系统点评：${commentMatch[1]}</span></div>`;
                         } else {
                            panelHTML += `<div class="system-quote">${value.replace(/「|」/g, '')}</div>`;
                         }
                    } else {
                        panelHTML += `
                            <div class="data-item">
                                <span class="data-key">${key}:</span>
                                <span class="data-value">${value}</span>
                            </div>`;
                    }
                }
                panel.innerHTML = panelHTML;
                contentArea.appendChild(panel);
            });

            const tabs = document.querySelectorAll('.tab');
            const panels = document.querySelectorAll('.content-panel');

            if (tabs.length > 0) {
                tabs[0].classList.add('active');
                panels[0].classList.add('active');
            }

            tabs.forEach(tab => {
                tab.addEventListener('click', () => {
                    tabs.forEach(t => t.classList.remove('active'));
                    panels.forEach(p => p.classList.remove('active'));
                    tab.classList.add('active');
                    document.getElementById(tab.dataset.target).classList.add('active');
                });
            });
        }

        const rawData = dataContainer.textContent.trim();
        if (rawData) {
            const parsedData = parseData(rawData);
            renderData(parsedData);
        }
    });
