  <script type="module">
    /* Populates the status widget with current variable values */
    function populateStatusWidget() {
      const all_variables = getAllVariables();

      /* Get values, default to 0 if not found */
      const darkeningValue = _.get(all_variables, 'stat_data.路线.黑化路线', 0);
      const redemptionValue = _.get(all_variables, 'stat_data.路线.救赎路线', 0);

      /* Update text values */
      $('#darkening-value-widget').text(`${darkeningValue} / 100`);
      $('#redemption-value-widget').text(`${redemptionValue} / 100`);

      /* Update progress bar widths */
      $('#darkening-bar-widget').css('width', `${darkeningValue}%`);
      $('#redemption-bar-widget').css('width', `${redemptionValue}%`);
    }

    /* Toggles the collapsible section */
    function toggleWidget($header) {
        const $content = $header.next('.widget-content');
        $content.toggleClass('expanded');

        const $arrow = $header.find('.widget-arrow');
        $arrow.css('transform', $content.hasClass('expanded') ? 'rotate(90deg)' : 'rotate(0deg)');
    }

    /* Sets up the initial state and event listeners */
    function initWidget() {
      populateStatusWidget();

      /* Set up click listener for the header */
      $('.widget-header').on('click', function() {
          toggleWidget($(this));
      });
    }

    /* Run init function safely after DOM is loaded */
    $(() => errorCatched(initWidget)());
  </script>
</head>
