    <script>

        let Music_Audio = new Audio();
        let Music_NowPlayName = "";
        let musicname = `$1`;
        let singer = `$2`;
        const playButton = document.getElementById('playButton');
        const playIcon = playButton.querySelector('.play-icon');
        const pauseIcon = playButton.querySelector('.pause-icon');
        const progress = document.querySelector(".progress-bar");
        const currentTimeSpan = document.getElementById("currentTime");
        const ShowInfo = document.querySelector('.ShowInfo');
        let currentTime;
        let CurrentId;

        // 监听其他播放器的播放事件
        eventOn("music_player_started", (playerId) => {
            // 如果是其他播放器在播放，而当前播放器也在播放，则暂停当前播放器
            if (playerId !== CurrentId && !Music_Audio.paused) {
                console.log(`收到播放器 ${playerId} 的播放事件，暂停当前播放器`);
                Music_Audio.pause();
                // 更新UI
                playIcon.style.display = 'block';
                pauseIcon.style.display = 'none';
            }
        });

        async function QQ_MusicGetUrl(name) {
            try {
                // 获取歌曲列表

                name = name.replace(/\s/g, "");

                let cover = "";

                const result = await Http_Get(`https://api.vkeys.cn/v2/music/tencent?word=${name}`);
                if (!result?.data?.length) {
                    QQ_Error("搜索歌曲失败");
                    return;
                }

                // 提取所有id
                let ids = [];
                for (const data of result.data) {
                    if (!cover && data.cover) {
                        cover = data.cover;
                    }
                    if (data.id && !data.song.match(/live/gi)) {
                        const obj = {
                            id: data.id,
                            song: data.song,
                            singer: data.singer
                        }
                        ids.push(obj);
                    }
                    if (data.grp) {
                        for (const grp of data.grp) {
                            if (grp.id && !grp.song.match(/live/gi)) {
                                const obj = {
                                    id: grp.id,
                                    song: grp.song,
                                    singer: grp.singer
                                }
                                ids.push(obj);
                            }
                        }
                    }
                }
                console.log(`id数量:${ids.length}`);
                // 遍历音质组检测可用音源
                for (const song of ids) {
                    try {
                        // 获取具体音源URL
                        console.log(`准备检测音源 ID:${song.id}`);
                        const r = await Http_Get(`https://api.vkeys.cn/v2/music/tencent?id=${song.id}`);
                        if (!r?.data?.url) continue;

                        // 异步检测音源可用性
                        const isAvailable = await checkAudioAvailability(r.data.url);
                        if (isAvailable) {
                            console.log(`找到可用音源: ${r.data.url}`);
                            return {
                                url: r.data.url,
                                cover: cover,
                                song: song.song,
                                singer: song.singer,
                                source: "QQ音乐"
                            };
                        }
                    } catch (e) {
                        console.warn(`音源检测失败: ${song.id}`, e);
                        return null;
                    }
                }
                return null;

                console.warn("没有找到可用音源");
            } catch (e) {
                console.warn("歌曲搜索异常");
                console.error("获取音源失败:", e);
                return null;
            }
        }

        async function WY_MusicGetUrl(name, singer) {
            name = name.replace(/\s/g, "");
            let url = `https://api.vkeys.cn/v2/music/netease?word=${name}`;
            if (singer) {
                url += `-${singer}`;
            }

            console.log(`网易云搜索url:${url}`);

            let result = await Http_Get(url);
            if (!result) {
                return;
            };

            let cover = "";
            let ids = [];
            for (const data of result.data) {
                if (!cover && data.cover) {
                    cover = data.cover;
                }
                if (data.id) {
                    const obj = {
                        song: data.song,
                        singer: data.singer,
                        id: data.id
                    }
                    ids.push(obj);
                }
            }

            for (const song of ids) {
                try {
                    // 获取具体音源URL
                    console.log(`准备检测音源 ID:${song.id}`);
                    const r = await Http_Get(`https://api.vkeys.cn/v2/music/netease?id=${song.id}`);
                    if (!r?.data?.url) continue;

                    // 异步检测音源可用性
                    const isAvailable = await checkAudioAvailability(r.data.url);
                    if (isAvailable) {
                        console.log(`找到可用音源: ${r.data.url}`);
                        return {
                            url: r.data.url,
                            cover: cover,
                            song: song.song,
                            singer: song.singer,
                            source: "网易"
                        };
                    }
                } catch (e) {
                    console.warn(`音源检测失败: ${song.id}`, e);
                }
            }
        }

        async function checkAudioAvailability(url) {
            return new Promise((resolve) => {
                // 创建测试用音频对象
                const tester = new Audio();
                let timer;

                // 成功加载元数据
                const onLoaded = () => {
                    cleanup();
                    resolve(true);
                };

                // 发生错误或超时
                const onError = () => {
                    cleanup();
                    resolve(false);
                };

                // 清理事件监听
                const cleanup = () => {
                    tester.removeEventListener('loadedmetadata', onLoaded);
                    tester.removeEventListener('error', onError);
                    clearTimeout(timer);
                    tester.src = ''; // 释放资源
                };

                // 设置检测参数
                tester.preload = 'metadata';
                tester.src = url;
                timer = setTimeout(onError, 3000); // 3秒超时

                // 绑定事件监听
                tester.addEventListener('loadedmetadata', onLoaded);
                tester.addEventListener('error', onError);
            });
        }

        function Http_Get(url) {
            return new Promise((resolve, reject) => {
                $.ajax({
                    url: url,
                    method: 'GET',
                    timeout: 10000,
                    success: function (data, status) {
                        resolve(data); // 成功时返回数据
                    },
                    error: function (xhr, status, error) {
                        if (status === 'timeout') {
                            console.error('请求超时，请检查网络或重试');
                        } else {
                            console.error('请求失败，错误信息：', error);
                        }
                        //reject(error); // 失败时抛出错误
                        resolve(null);
                    }
                });
            });
        }

        async function GetCurrentMessages() {
            const CurrentMessageId = await getLastMessageId();
            const Messages = await getChatMessages(CurrentMessageId);
            if (!Messages) {
                console.log(`获取楼层记录失败`);
                return "";
            }
            let msg = Messages[0].message;
            //console.log(`最新楼层消息:${msg}`);
            return msg;
        }

        async function MusicPlay() {
            const music = musicname.replace(/\s/g, "");
            if (music == Music_NowPlayName) {
                console.log(`是正在播放的歌曲,返回`);
                //return;
            }

            Music_NowPlayName = music;
            console.log(`获取到歌名:${music}`);
            let result = await WY_MusicGetUrl(music, singer);
            if (!result || !result.url) {
                console.log(`网易云获取失败,开始在QQ音乐中搜索`);
                result = await QQ_MusicGetUrl(music);
                if (!result || !result.url) {
                    triggerSlash(`/echo severity=error 搜索歌曲失败!`);
                    return;
                }
            };
            console.log(`开始播放音乐:\n${music}\n${result.url}`);
            ShowInfo.textContent = `实际播放：${result.source} - ${result.song} - ${result.singer}`;
            ShowInfo.style.display = "block";

            try {
                Music_Audio.src = result.url;
                await Music_Audio.play();
                //await audioEnable({ type: 'bgm' });
                //await audioImport({ type: 'bgm' }, result.url);
                console.log(`已开始播放:${result.url}`);
            }
            catch (e) {
                triggerSlash(`/echo severity=error 播放失败`);
                triggerSlash(`/echo severity=error ${e}`);
            }

        }

        // 增加音频状态监听
        Music_Audio.addEventListener("ended", () => {
            Music_NowPlayName = "";
            playIcon.style.display = 'block';
            pauseIcon.style.display = 'none';
        });

        Music_Audio.addEventListener("error", () => {
            Music_NowPlayName = "";
            playIcon.style.display = 'block';
            pauseIcon.style.display = 'none';
            triggerSlash(`/echo severity=error 播放出错`);
        });

        Music_Audio.addEventListener("loadedmetadata", function () {
            const totalTime = formatTime(Music_Audio.duration);
            document.getElementById("duration").textContent = totalTime;
            console.log("总时长:", totalTime);
        });

        // 通过 addEventListener 监听
        Music_Audio.addEventListener('playing', () => {
            eventEmit("music_player_started", CurrentId);
        });

        // 实时获取播放进度百分比    
        Music_Audio.addEventListener("timeupdate", function () {
            const percent = (Music_Audio.currentTime / Music_Audio.duration) * 100 || 0;
            progress.style.width = `${percent}%`;

            currentTime = Music_Audio.currentTime;
            //console.log(`已播放:${currentTime}`);
            currentTimeSpan.textContent = formatTime(Music_Audio.currentTime);
        });

        function formatTime(seconds) {
            // 取整并处理 NaN（如音频未加载时）
            const sec = Math.floor(seconds || 0);
            const minutes = Math.floor(sec / 60);
            const remainingSeconds = sec % 60;
            // 补零（如 5 秒 → "05"）
            return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
        }

        async function init() {
            CurrentId = await getCurrentMessageId();
            if (CurrentId == await getLastMessageId()) {
                button_click();
            }
        }

        init();

        async function button_click() {
            console.log(`点击了按钮`);
            if (Music_Audio.paused) {
                playIcon.style.display = 'none';
                pauseIcon.style.display = 'block';
                // 这里可以添加实际播放逻辑
                console.log(`开始播放`);

                if (Music_Audio.src) {
                    try {
                        Music_Audio.currentTime = currentTime;
                        await Music_Audio.play();
                    } catch (e) {
                        triggerSlash(`/echo severity=error ${e}`);
                    }
                }
                else {
                    MusicPlay();
                }
            } else {
                playIcon.style.display = 'block';
                pauseIcon.style.display = 'none';
                // 这里可以添加实际暂停逻辑
                console.log(`暂停`);
                Music_Audio.pause();
            }
        }

        // 添加播放控制逻辑
        playButton.addEventListener('click', button_click);


    </script>

</body>

</html>
