        .checkbox-option input[type="checkbox"] {
            display: none; /* 隐藏原生checkbox */
        }
        .checkbox-option .custom-checkbox {
            width: 16px;
            height: 16px;
            border: 2px solid var(--interact-color);
            border-radius: 3px;
            margin-right: 10px;
            transition: all 0.2s;
            display: inline-flex;
            align-items: center;
            justify-content: center;
        }
        .checkbox-option .custom-checkbox::after {
            content: '✔';
            color: var(--highlight-color);
            font-size: 14px;
            transform: scale(0);
            transition: transform 0.2s;
        }
        .checkbox-option input:checked + .custom-checkbox {
            background-color: var(--interact-color);
            border-color: var(--highlight-color);
        }
        .checkbox-option input:checked + .custom-checkbox::after {
            transform: scale(1);
        }

        .fill-in-blank {
            margin-top: 10px;
        }


        /* 提交按钮 */
        .submit-button {
            grid-column: 1 / -1;
            padding: 12px 20px;
            background-color: var(--highlight-color);
            color: #000;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            font-size: 1.2em;
            font-family: var(--font-title);
            transition: all 0.3s ease;
            position: relative;
            overflow: hidden;
            justify-self: center;
            width: 250px;
            letter-spacing: 2px;
        }
        .submit-button:hover {
            background-color: #fff;
            color: #000;
            box-shadow: var(--neon-glow-yellow);
        }
        .submit-button:active {
            transform: scale(0.98);
        }

        /* --- 开场白列表样式 (录影带/卡带风格) --- */
        .opening-list {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
            gap: 25px;
        }
        .opening-item {
            background: linear-gradient(145deg, #383838, #202020);
            border: 1px solid #555;
            border-radius: 8px;
            padding: 15px;
            cursor: pointer;
            transition: all 0.3s ease;
            position: relative;
            box-shadow: 5px 5px 10px rgba(0,0,0,0.5);
            -webkit-user-select: none;
            user-select: none;
        }
        .opening-item:hover {
            transform: translateY(-5px) scale(1.02);
            box-shadow: 8px 8px 15px rgba(0,0,0,0.7), 0 0 10px var(--interact-color);
        }
        .opening-item::before { /* 模拟磁带标签 */
            content: '';
            position: absolute;
            top: 10px;
            left: 10px;
            right: 10px;
            height: 40px;
            background-color: #d1c7b7;
            border: 1px solid #a09483;
            border-radius: 3px;
        }
        .opening-item-content {
            position: relative;
            z-index: 1;
        }
        .opening-item h4 {
            font-family: 'Courier New', Courier, monospace;
            font-size: 1.2em;
            color: #333;
            text-align: center;
            margin-top: 5px;
            height: 35px; /* 限制标题高度 */
            overflow: hidden;
        }
        .opening-item p {
            font-size: 0.9em;
            color: var(--text-color);
            margin-top: 35px;
            line-height: 1.5;
            height: 90px;
            overflow: hidden;
            -webkit-mask-image: linear-gradient(to bottom, black 70%, transparent 100%);
            mask-image: linear-gradient(to bottom, black 70%, transparent 100%);
        }

        /* 互动气泡 */
        .interaction-bubble {
            position: absolute;
            bottom: 110%;
            left: 50%;
            transform: translateX(-50%);
            background-color: var(--highlight-color);
            color: #000;
            padding: 8px 12px;
            border-radius: 20px;
            font-size: 0.9em;
            font-family: var(--font-body);
            white-space: nowrap;
            opacity: 0;
            visibility: hidden;
            transition: opacity 0.3s, visibility 0.3s;
            z-index: 10;
        }
        .interaction-bubble::after { /* 气泡小角 */
            content: '';
            position: absolute;
            top: 100%;
            left: 50%;
            transform: translateX(-50%);
            border-width: 5px;
            border-style: solid;
            border-color: var(--highlight-color) transparent transparent transparent;
        }
        .form-group:hover .interaction-bubble {
            opacity: 1;
            visibility: visible;
        }

    </style>
</head>
<body>
