    <script>
        // --- 交互逻辑 ---

        // 1. 角色档案表单提交
        document.getElementById('user-profile-form').addEventListener('submit', function(event) {

            event.preventDefault();

            // 获取选中的身份
            const identities = Array.from(document.querySelectorAll('input[name="identity"]:checked')).map(el => el.value);
            const otherIdentity = document.querySelector('input[name="identity_other"]').value.trim();
            if (otherIdentity) {
                identities.push(otherIdentity);
            }

            // 获取选中的性格
            const personalities = Array.from(document.querySelectorAll('input[name="personality"]:checked')).map(el => el.value);
            const otherPersonality = document.querySelector('input[name="personality_other"]').value.trim();
            if (otherPersonality) {
                personalities.push(otherPersonality);
            }

            const userProfile = {
                name: document.getElementById('user-name').value,
                nickname: document.getElementById('user-nickname').value || '无',
                gender: document.getElementById('user-gender').value || '未透露',
                age: document.getElementById('user-age').value || '未透露',
                birthday: document.getElementById('user-birthday').value || '未透露',
                identities: identities.length > 0 ? identities : ['未设定'],
                personalities: personalities.length > 0 ? personalities : ['未设定'],
                background: document.getElementById('user-background').value || '一段尘封的往事。',
                likes_dislikes: document.getElementById('user-likes-dislikes').value || '秘密。'
            };

            const profileJson = JSON.stringify(userProfile, null, 2);

            // -- 重要：请在此处替换为你的世界书信息 --
            const wiFilename = "😎拥着你当初温馨再涌现";
            const wiUid = "16";

            if (wiFilename === "YOUR_WI_FILENAME" || wiUid === "YOUR_WI_UID") {
                 if (typeof triggerSlash === 'function') {
                    triggerSlash(`/echo severity=error 喂！未喺代码度填你嘅世界书名同UID啊！`);
                }
                return;
            }

            const command = `/setentryfield file="${wiFilename}" uid=${wiUid} field=content ${profileJson} | /echo severity=success 档案更新成功！祝你喺呢个江湖，走出自己嘅路。`;

            if (typeof triggerSlash === 'function') {
                triggerSlash(command);
            } else {
                console.warn("SillyTavern's triggerSlash function not found.");
                alert("档案已生成（调试模式），请查看控制台输出的命令。");
                console.log("Generated Command:", command);
            }
        });

        // 2. 开场白列表动态加载和跳转
        async function loadOpenings() {
            const container = document.getElementById('opening-list-container');
            if (!container) return;

            if (typeof getChatMessages !== 'function') {
                container.innerHTML = '<p style="color: var(--danger-color);">API未找到，无法加载开场白。</p>';
                return;
            }

            try {
                const messages = await getChatMessages("0", { include_swipe: true });

                if (!messages || messages.length === 0 || !messages[0].swipes || messages[0].swipes.length <= 1) {
                    container.innerHTML = '<p style="color: #999;">暂无其他故事线。</p>';
                    return;
                }

                const swipes = messages[0].swipes;
                for (let i = 1; i < swipes.length; i++) {
                    const content = swipes[i];
                    let title = `故事 ${i}`;
                    let description = "点击此处，开始你嘅故事。";

                    const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
                    if (titleMatch) title = titleMatch[1].trim();

                    const descMatch = content.match(/<!--\s*desc:\s*(.*?)(?=\s*-->)-->/s);
                    if (descMatch) description = descMatch[1].trim();

                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.innerHTML = `<div class="opening-item-content"><h4>${title}</h4><p>${description}</p></div>`;

                    item.addEventListener('click', (event) => {
                        // 点击反馈
                        const rect = event.currentTarget.getBoundingClientRect();
                        const ripple = document.createElement('span');
                        ripple.style.position = 'absolute';
                        ripple.style.borderRadius = '50%';
                        ripple.style.backgroundColor = 'rgba(0, 224, 255, 0.7)';
                        ripple.style.transform = 'scale(0)';
                        ripple.style.animation = 'ripple 0.6s linear';
                        ripple.style.left = `${event.clientX - rect.left}px`;
                        ripple.style.top = `${event.clientY - rect.top}px`;
                        event.currentTarget.appendChild(ripple);
                        setTimeout(() => ripple.remove(), 600);

                        // 延迟切换，让动画播完
                        setTimeout(() => switchToOpening(i), 300);
                    });

                    container.appendChild(item);
                }

            } catch (error) {
                console.error('加载开场白失败:', error);
                container.innerHTML = `<p style="color: var(--danger-color);">加载开场白出错，请检查控制台。</p>`;
            }
        }

        // 定义点击波纹动画
        const styleSheet = document.createElement("style");
        styleSheet.innerText = `
            @keyframes ripple {
                to {
                    transform: scale(4);
                    opacity: 0;
                }
            }
        `;
        document.head.appendChild(styleSheet);


        async function switchToOpening(swipeId) {
            if (typeof setChatMessage !== 'function') return;
            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
                    await setChatMessage(messages[0].swipes[swipeId], 0, {
                        swipe_id: swipeId,
                        refresh: 'display_and_render_current'
                    });
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                if (typeof triggerSlash === 'function') {
                    triggerSlash(`/echo severity=error 切换故事线失败！`);
                }
            }
        }

        window.addEventListener('load', loadOpenings);
    </script>
